#include <iostream>

using namespace std;
char charArray[] = "";
int numArray[1000];
//store 1000 number sequence in char array
//array to store chars into ints

long long int largestNproduct(int n) {//returns largest product of n numbers in 1000 number sequence
    long long int adNums[n]; //create array to store sequence of n Adjacent numbers
    long long int tempProd;
    long long int product=0; //variable to store largest product
    int i;
    for(int start=0;start<1001-n;start++){ //iterate through number blob
        for( i=0;i<n;i++){ //create sequences of n-digit arrays
            adNums[i]=(long long)numArray[start+i];
        }
        tempProd=1;
        for(i=0;i<n;i++) {
            tempProd*=adNums[i]; //multiple all numbers in array and store in tempProd
        }
        if(tempProd>product) { //if tempProd is the largest generated product store it
            product = tempProd;
        }
    }
    return product; //return the largest generated product

}

void toNumArray(char* a, int* b){ //converts char array a to int array b
    for(int i = 0; i<1000; i++){
        numArray[i] = (charArray[i] - '0'); //change char to int
    }
}

int main() {
    toNumArray(charArray, numArray);
    cout<<largestNproduct(13);
}